import java.time.{LocalDateTime, ZoneOffset}

import org.scalatest.FunSuite
import util.PolicyGenerator

class PolicyGeneratorTest extends FunSuite {
  // http://docs.aws.amazon.com/AmazonS3/latest/API/sigv4-post-example.html
  test("aws documentation example") {
    val secretKey = "wJalrXUtnFEMI/K7MDENG/bPxRfiCYEXAMPLEKEY"

    val base64Policy = ""
    val timestamp = LocalDateTime.of(2015, 12, 29, 0, 0, 0).toInstant(ZoneOffset.UTC)

    val expected = "8afdbf4008c03f22c2cd3cdb72e4afbb1f6a588f3255ac628749a66d7f09699e"
    val actual = PolicyGenerator.sign(base64Policy, secretKey, "us-east-1", timestamp)

    assert(actual === expected)
  }
}
